import { __decorate } from "tslib";
import { Component } from '@angular/core';
import { DataService } from './data.service';
let AppComponent = class AppComponent {
    constructor(dataService) {
        this.dataService = dataService;
        this.allInfo = {};
        this.allComments = [];
    }
    async load() {
        this.allInfo = await this.dataService.getUsers();
        this.allComments = this.allInfo.comments;
    }
    ;
    async ngOnInit() {
        await this.load();
    }
    //increment function
    incrementScore(id) {
        for (let i = 0; i < this.allComments.length; i++) {
            if (this.allComments[i].id === id) {
                this.allComments[i].score++;
            }
            if (this.allComments[i].replies) {
                for (let j = 0; j < this.allComments[i].replies.length; j++) {
                    if (this.allComments[i].replies[j].id === id) {
                        this.allComments[i].replies[j].score++;
                    }
                }
            }
        }
    }
    decrementScore(id) {
        for (let i = 0; i < this.allComments.length; i++) {
            if (this.allComments[i].id === id) {
                this.allComments[i].score--;
            }
            if (this.allComments[i].replies) {
                for (let j = 0; j < this.allComments[i].replies.length; j++) {
                    if (this.allComments[i].replies[j].id === id) {
                        this.allComments[i].replies[j].score--;
                    }
                }
            }
        }
    }
};
AppComponent = __decorate([
    Component({
        selector: 'app-root',
        templateUrl: './app.component.html',
        styleUrls: ['./app.component.scss'],
        providers: [DataService]
    })
], AppComponent);
export { AppComponent };
