import { __decorate } from "tslib";
import { Component } from '@angular/core';
import { DataService } from './data.service';
let AppComponent = class AppComponent {
    constructor(dataService) {
        this.dataService = dataService;
        this.newCommentText = '';
        this.allInfo = {};
        this.allComments = [];
    }
    async load() {
        this.allInfo = await this.dataService.getUsers();
        this.allComments = this.allInfo.comments;
    }
    ;
    async ngOnInit() {
        await this.load();
    }
    newComment() {
        const date = new Date();
        const newContent = {
            id: Date.now(),
            content: this.newCommentText,
            creadetAt: `${date.getDay()}/${date.getMonth() + 1}/${date.getFullYear()}`,
            score: 0,
            user: {
                image: this.allInfo.currentUser.image,
                username: this.allInfo.currentUser.username,
            },
            replies: [],
        };
        this.allComments.push(newContent);
    }
    replyComment(obj) {
        const date = new Date();
        const newContent = {
            id: Date.now(),
            content: obj.content,
            creadetAt: `${date.getDay()}/${date.getMonth() + 1}/${date.getFullYear()}`,
            score: 0,
            user: {
                image: this.allInfo.currentUser.image,
                username: this.allInfo.currentUser.username,
            }
        };
        for (let i = 0; i < this.allComments.length; i++) {
            if (this.allComments[i].id === obj.id) {
                this.allComments[i].replies.push(newContent);
            }
        }
    }
    //increment function
    incrementScore(id) {
        for (let i = 0; i < this.allComments.length; i++) {
            if (this.allComments[i].id === id && this.allComments[i].score >= 0) {
                this.allComments[i].score++;
            }
            if (this.allComments[i].replies) {
                for (let j = 0; j < this.allComments[i].replies.length; j++) {
                    if (this.allComments[i].replies[j].id === id && this.allComments[i].replies[j].score >= 0) {
                        this.allComments[i].replies[j].score++;
                    }
                }
            }
        }
    }
    //decrement function
    decrementScore(id) {
        for (let i = 0; i < this.allComments.length; i++) {
            if (this.allComments[i].id === id && this.allComments[i].score > 0) {
                this.allComments[i].score--;
            }
            if (this.allComments[i].replies) {
                for (let j = 0; j < this.allComments[i].replies.length; j++) {
                    if (this.allComments[i].replies[j].id === id && this.allComments[i].replies[j].score > 0) {
                        this.allComments[i].replies[j].score--;
                    }
                }
            }
        }
    }
    deleteComment(id) {
        this.allComments = this.allComments.filter((comments) => {
            if (comments.replies) {
                comments.replies = comments.replies.filter((replyComment) => {
                    return replyComment.id !== id;
                });
            }
            return comments.id !== id;
        });
    }
};
